import React, { useEffect, useState } from "react";
import axios from "axios";
import WorkoutForm from "./WorkoutForm";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const navigate = useNavigate();
  const [workouts, setWorkouts] = useState([]);
  const userToken = localStorage.getItem("token");
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    if (!userToken) {
      navigate("/login");
      return;
    }

    // decode token to get user ID (simplified)
    const base64Url = userToken.split(".")[1];
    const decoded = JSON.parse(atob(base64Url));
    setUserId(decoded.id);
  }, [userToken, navigate]);

  const fetchWorkouts = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/workouts", {
        params: { userId },
      });
      setWorkouts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this workout?")) return;
    await axios.delete(`http://localhost:5000/api/workouts/${id}`);
    fetchWorkouts();
  };

  useEffect(() => {
    if (userId) fetchWorkouts();
  }, [userId]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="card p-4 shadow">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3>üèãÔ∏è Workout Dashboard</h3>
        <button className="btn btn-danger btn-sm" onClick={handleLogout}>
          Logout
        </button>
      </div>

      <WorkoutForm userId={userId} onWorkoutAdded={fetchWorkouts} />

      <h5 className="text-info mb-3">Your Workouts</h5>
      {workouts.length === 0 ? (
        <p className="text-muted">No workouts yet. Add one above!</p>
      ) : (
        <ul className="list-group">
          {workouts.map((w) => (
            <li
              key={w._id}
              className="list-group-item bg-dark text-light mb-2 rounded border-success"
            >
              <div className="d-flex justify-content-between align-items-center">
                <div>
                  <strong>{w.workoutName}</strong> ({w.duration} mins)
                  <br />
                  <small>{new Date(w.date).toLocaleDateString()}</small>
                  {w.notes && <p className="mb-0 text-muted">{w.notes}</p>}
                </div>
                <button
                  className="btn btn-outline-danger btn-sm"
                  onClick={() => handleDelete(w._id)}
                >
                  üóë Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Dashboard;
