import React, { useState } from "react";
import axios from "axios";

function Register() {
  const [form, setForm] = useState({
    full_name: "",
    email: "",
    username: "",
    password: "",
    confirmPassword: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/users/register", form);
      setMessage(res.data.msg);
    } catch (err) {
      setMessage(err.response.data.msg);
    }
  };

  return (
    <div className="card p-4 shadow">
      <h4>Register</h4>
      {message && <p className="text-danger">{message}</p>}
      <form onSubmit={handleSubmit}>
        <input name="full_name" className="form-control mb-2" placeholder="Full Name" onChange={handleChange} />
        <input name="email" className="form-control mb-2" placeholder="Email" onChange={handleChange} />
        <input name="username" className="form-control mb-2" placeholder="Username" onChange={handleChange} />
        <input name="password" type="password" className="form-control mb-2" placeholder="Password" onChange={handleChange} />
        <input name="confirmPassword" type="password" className="form-control mb-2" placeholder="Confirm Password" onChange={handleChange} />
        <button className="btn btn-primary w-100">Register</button>
      </form>
    </div>
  );
}

export default Register;
